import React from 'react';
import { FilterValuesType } from '../App';
import '../App.css';

export type ButtonAddType = {
    onCockHandler:(filterValue:FilterValuesType) => void
    filter: FilterValuesType
    value: FilterValuesType
}
export const ButtonAdd:React.FC<ButtonAddType> = (props) => {
    const clickHandler = () => props.onCockHandler(props.value )
    const changeFilteredColor =  props.filter == props.value ? 'active-filter' : ''
    return (
            <button className={changeFilteredColor} onClick={clickHandler}>{props.value}</button>

    )
}

export default ButtonAdd;
